configuration.source:

                                for (java.io.File file=new java.io.File(new java.net.URI('${project.baseUri}')); file != null; file = file.getParentFile()) {
                                    if (!file.isDirectory()) {
                                        continue
                                    }

                                    java.io.File    testFile=new java.io.File(file, '.gitmodules')
                                    if (testFile.canRead()) {
                                        project.properties['insight.root.dir'] = file.getAbsolutePath()
										return
                                    }
                                }

                                System.err.println("Failed to detected Insight root dir")


								if (!session.goals.contains("deploy")) {
									return;
								}

                                if ('${project.version}'.contains("SNAPSHOT")) {
                                    session.currentProject.distributionManagement.snapshotRepository.id = project.properties['snapshot.repo.id']
                                    session.currentProject.distributionManagement.snapshotRepository.name = project.properties['snapshot.repo.name']
                                    session.currentProject.distributionManagement.snapshotRepository.url = project.properties['snapshot.dist.url']

									project.properties['deploy.repo.id'] = project.properties['snapshot.repo.id']
									project.properties['deploy.repo.name'] = project.properties['snapshot.repo.name']
									project.properties['deploy.repo.url'] = project.properties['snapshot.dist.url']
                                } else if ('${project.version}'.contains("RELEASE") || '${project.version}'.contains(".SR")) {
                                    session.currentProject.distributionManagement.repository.id = project.properties['release.repo.id']
                                    session.currentProject.distributionManagement.repository.name = project.properties['release.repo.name']
                                    session.currentProject.distributionManagement.repository.url = project.properties['release.dist.url']

									project.properties['deploy.repo.id'] = project.properties['release.repo.id']
									project.properties['deploy.repo.name'] = project.properties['release.repo.name']
									project.properties['deploy.repo.url'] = project.properties['release.dist.url']
                                } else {
                                    session.currentProject.distributionManagement.repository.id = project.properties['milestone.repo.id']
                                    session.currentProject.distributionManagement.repository.name = project.properties['milestone.repo.name']
                                    session.currentProject.distributionManagement.repository.url = project.properties['milestone.dist.url']

									project.properties['deploy.repo.id'] = project.properties['milestone.repo.id']
									project.properties['deploy.repo.name'] = project.properties['milestone.repo.name']
									project.properties['deploy.repo.url'] = project.properties['milestone.dist.url']
                                }
